mapper.select:
SELECT rs.USER_ID AS userId , rs. RECOMMEND_STOCK_ID AS recommendStockId , rs.CONTENT AS content , s.STOCK_ID AS stockId , s.STOCK_NAME as stockName , s.SHORT_CODE AS shortCode FROM RECOMMEND_STOCK rs JOIN STOCK s ON rs.STOCK_ID = s.STOCK_ID WHERE USER_ID = #{userId}
AND DATE_FORMAT('YYYY-MM-DD', rs.CREATED_AT) LIKE CONCAT('%', #{date}, '%');
AND DATE_FORMAT('YYYY-MM-DD', rs.CREATED_AT) = DATE_FORMAT('YYYY-MM-DD', CURRENT_TIMESTAMP)
