mapper.select:
SELECT DATE_FORMAT(createdAt, '%d') AS day , shortCode , stockName , content , price , (tb.closedPrice - tb.price) AS changeAmount , TRUNCATE((tb.closedPrice - tb.price) / tb.price, 2) AS changeAmountRate FROM (SELECT s.STOCK_ID AS stockId , s.SHORT_CODE AS shortCode , s.STOCK_NAME as stockName , rs.RECOMMEND_STOCK_ID AS recommendStockId , rs.CONTENT AS content , rs.PRICE AS price , (SELECT CLOSED_PRICE FROM STOCK_HISTORY sh WHERE STOCK_HISTORY_ID = (SELECT MAX(STOCK_HISTORY_ID) FROM STOCK_HISTORY sh WHERE sh.STOCK_ID = s.STOCK_ID AND sh.SHORT_CODE = s.SHORT_CODE) AND sh.STOCK_ID = s.STOCK_ID AND sh.SHORT_CODE = s.SHORT_CODE) AS closedPrice , rs.CREATED_AT AS createdAt FROM RECOMMEND_STOCK rs JOIN STOCK s ON rs.STOCK_ID = s.STOCK_ID WHERE USER_ID = #{userId}
AND DATE_FORMAT(rs.CREATED_AT, '%Y-%m-%d') LIKE CONCAT('%', #{date}, '%')
AND DATE_FORMAT(rs.CREATED_AT, '%Y-%m-%d') = DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d')
) tb ORDER BY 1 DESC

SELECT SHORT_CODE
FROM STOCK
WHERE STOCK_ID = #{stockId}
