mapper.select:
SELECT s.STOCK_ID AS stockId , s.SHORT_CODE AS shortCode , s.STOCK_NAME as stockName , rs.RECOMMEND_STOCK_ID AS recommendStockId , rs.USER_ID AS userId , rs.CONTENT AS content FROM RECOMMEND_STOCK rs JOIN STOCK s ON rs.STOCK_ID = s.STOCK_ID WHERE USER_ID = #{userId}
AND DATE_FORMAT(rs.CREATED_AT, '%Y-%m-%d') LIKE CONCAT('%', #{date}, '%');
AND DATE_FORMAT(rs.CREATED_AT, '%Y-%m-%d') = DATE_FORMAT(CURRENT_TIMESTAMP, '%Y-%m-%d')
